mapper.update:

update	bitstoa.tbl_event
set c_tm =  now()



	, c_tm = #{C_TM}




	, `name` = #{NAME}




	, `symbol` = #{SYMBOL}




	, view_symbol = #{VIEW_SYMBOL}




	, family_no = #{FAMILY_NO}




	, `inout` = #{INOUT}




	, in_fee = #{IN_FEE}




	, out_fee = #{OUT_FEE}




	, `show` = #{SHOW}




	, wallet_open = #{WALLET_OPEN}




	, wallet_type = #{WALLET_TYPE}




	, symbol_txid = #{SYMBOL_TXID}




	, wallet_enable = #{WALLET_ENABLE}



where	`no` = #{NO}

mapper.select:

SELECT	tw.no
		, tw.c_tm
		, tw.name
		, tw.symbol
		, tw.view_symbol
		, tw.family_no
		, tw.inout
		, tw.in_fee
		, tw.out_fee
		, tw.show
		, tw.wallet_open
		, tw.wallet_type
		, tw.symbol_txid
		, tw.wallet_enable
FROM bitstoa.tbl_event tw
WHERE 1=1



	AND tw.no=#{NO}




		AND tw.c_tm = #{C_TM}




		AND tw.name = #{NAME}




		AND tw.symbol = #{SYMBOL}




		AND tw.view_symbol = #{VIEW_SYMBOL}




		AND tw.show = #{SHOW}




		AND tw.wallet_open = #{WALLET_OPEN}




		AND tw.wallet_type = #{WALLET_TYPE}




		AND tw.symbol_txid = #{SYMBOL_TXID}




		AND tw.wallet_enable = #{WALLET_ENABLE}




		AND DATE(tw.c_tm) BETWEEN #{FAMILY_NODATE} AND #{INOUTDATE}



ORDER BY 1 DESC
LIMIT	#{PAGENO}, #{ROWS}


SELECT COUNT(*) COUNT
FROM bitstoa.tbl_event tw 
WHERE 1=1



	AND tw.no=#{NO}




		AND tw.c_tm = #{C_TM}




		AND tw.name = #{NAME}




		AND tw.symbol = #{SYMBOL}




		AND tw.view_symbol = #{VIEW_SYMBOL}




		AND tw.show = #{SHOW}




		AND tw.wallet_open = #{WALLET_OPEN}




		AND tw.wallet_type = #{WALLET_TYPE}




		AND tw.symbol_txid = #{SYMBOL_TXID}




		AND tw.wallet_enable = #{WALLET_ENABLE}




		AND tw.wallet_enable = #{WALLET_ENABLE}




		AND DATE(tw.c_tm) BETWEEN #{FAMILY_NODATE} AND #{INOUTDATE};

SELECT tw.no , tw.c_tm , tw.name , tw.symbol , tw.view_symbol , tw.family_no , tw.inout , tw.out_fee , tw.show , tw.wallet_open , tw.wallet_type , tw.symbol_txid , tw.wallet_enable FROM bitstoa.tbl_event tw WHERE 1=1
AND tw.no=#{NO}
AND tw.symbol=#{SYMBOL}
LIMIT 0, 1
